mapper.select:
/* UserMapper.getUser: 사용자 조건 검색 : by 김경식 */
SELECT
    *
FROM
    USER


        AND ID = #{ID}

mapper.update:
/* UserMapper.updateUser : 사용자 정보 수정 : by 김경식 */
UPDATE USER
SET
    NAME = #{NAME} 
    , EMAIL = #{EMAIL}
WHERE
    ID = #{ID}

mapper.delete:
/* UserMapper.deleteUser : 사용자 정보 삭제 : by 김경식 */
DELETE FROM USER
WHERE
    ID = #{ID}

mapper.insert:
/* UserMapper.insertUser : 사용자 정보 추가 : by 김경식 */
INSERT INTO USER(
    ID
    , NAME
    , EMAIL
)
VALUES (
    #{ID}
    , #{NAME}
    , #{EMAIL}
)
